# Script PowerShell para verificar salud de servicios AWS
# Verifica que todos los recursos estรฉn funcionando correctamente

Write-Host "๐ฅ Verificando salud de servicios AWS..." -ForegroundColor Cyan
Write-Host ""

$allHealthy = $true

# 1. Verificar ECS Cluster
Write-Host "๐ฆ Verificando ECS Cluster..." -ForegroundColor Yellow
try {
    $clusters = aws ecs list-clusters --query 'clusterArns' --output text
    if ($clusters) {
        Write-Host "โ ECS Clusters encontrados: $clusters" -ForegroundColor Green
        
        # Verificar servicios
        $services = aws ecs list-services --cluster backend-prod-cluster --query 'serviceArns' --output text
        if ($services) {
            Write-Host "โ Servicios ECS activos: $services" -ForegroundColor Green
        } else {
            Write-Host "โ๏ธ  No se encontraron servicios ECS" -ForegroundColor Yellow
            $allHealthy = $false
        }
    } else {
        Write-Host "โ No se encontraron clusters ECS" -ForegroundColor Red
        $allHealthy = $false
    }
} catch {
    Write-Host "โ Error al verificar ECS: $_" -ForegroundColor Red
    $allHealthy = $false
}

Write-Host ""

# 2. Verificar RDS
Write-Host "๐๏ธ  Verificando RDS..." -ForegroundColor Yellow
try {
    $rdsInstances = aws rds describe-db-instances --query 'DBInstances[*].[DBInstanceIdentifier,DBInstanceStatus]' --output table
    if ($rdsInstances) {
        Write-Host $rdsInstances
        $status = aws rds describe-db-instances --query 'DBInstances[0].DBInstanceStatus' --output text
        if ($status -eq "available") {
            Write-Host "โ RDS estรก disponible" -ForegroundColor Green
        } else {
            Write-Host "โ๏ธ  RDS estรก en estado: $status" -ForegroundColor Yellow
            $allHealthy = $false
        }
    } else {
        Write-Host "โ No se encontraron instancias RDS" -ForegroundColor Red
        $allHealthy = $false
    }
} catch {
    Write-Host "โ Error al verificar RDS: $_" -ForegroundColor Red
    $allHealthy = $false
}

Write-Host ""

# 3. Verificar ALB
Write-Host "โ๏ธ  Verificando Application Load Balancer..." -ForegroundColor Yellow
try {
    $albState = aws elbv2 describe-load-balancers --query 'LoadBalancers[0].State.Code' --output text
    if ($albState -eq "active") {
        Write-Host "โ ALB estรก activo" -ForegroundColor Green
    } else {
        Write-Host "โ๏ธ  ALB estรก en estado: $albState" -ForegroundColor Yellow
        $allHealthy = $false
    }
} catch {
    Write-Host "โ Error al verificar ALB: $_" -ForegroundColor Red
    $allHealthy = $false
}

Write-Host ""

# 4. Verificar costos del mes
Write-Host "๐ฐ Verificando costos del mes..." -ForegroundColor Yellow
try {
    $currentDate = Get-Date -Format "yyyy-MM-dd"
    $monthStart = (Get-Date -Day 1).ToString("yyyy-MM-dd")
    
    $cost = aws ce get-cost-and-usage `
        --time-period Start=$monthStart,End=$currentDate `
        --granularity MONTHLY `
        --metrics BlendedCost `
        --query 'ResultsByTime[0].Total.BlendedCost.Amount' `
        --output text
    
    if ($cost) {
        $costNum = [double]$cost
        Write-Host "๐ต Costo actual del mes: `$$([math]::Round($costNum, 2)) USD" -ForegroundColor Cyan
        
        if ($costNum -gt 50) {
            Write-Host "โ๏ธ  Costos altos detectados (>$50 USD)" -ForegroundColor Yellow
        } elseif ($costNum -gt 25) {
            Write-Host "โ๏ธ  Costos moderados (>$25 USD)" -ForegroundColor Yellow
        } else {
            Write-Host "โ Costos dentro del rango esperado" -ForegroundColor Green
        }
    }
} catch {
    Write-Host "โ๏ธ  No se pudieron obtener costos (puede requerir permisos adicionales)" -ForegroundColor Yellow
}

Write-Host ""
